/* Also important stuff about editor */
[aria-label="Code editor"] {
    background: black !important;  /* Editor background */
}

.cm-selectionLayer .cm-selectionBackground {
    background-color: var(--chakra-colors-gray-50) !important;  /* Highlight for selected text */
}

.cm-focused .cm-cursor {
    border-left: 1.2px solid white !important;  /* Cursor */
}

.cm-gutters {  /* Line numbers and gutter background */
    background-color: var(--chakra-colors-gray-25) !important;
    color: var(--chakra-colors-gray-200) !important;
}

/* Active line gutter color
Note that I inverted --chakra-colors-black so it means white now */
.cm-activeLineGutter {
    color: var(--chakra-colors-black) !important;
}


/* Dark scrollbar styling (AI-generated) */
.cm-scroller::-webkit-scrollbar {
    width: 12px; /* Width of the scrollbar */
}

.cm-scroller::-webkit-scrollbar-track {
    background: #2a2a2a; /* Dark track background */
}

.cm-scroller::-webkit-scrollbar-thumb {
    background: #555555; /* Dark thumb color */
    border-radius: 6px; /* Rounded corners */
}

.cm-scroller::-webkit-scrollbar-thumb:hover {
    background: #777777; /* Lighter color on hover */
}

.cm-scroller::-webkit-scrollbar-corner {
    background: #2a2a2a; /* Match the dark track background */
}

/* Tooltip */
.ͼ10 .cm-tooltip, .ͼ13 .cm-tooltip {
    background-color: var(--chakra-colors-gray-25) !important;  /* Tooltip background color */
}

.ͼ10 .cm-tooltip.cm-tooltip-autocomplete > ul > li[aria-selected], .ͼ13 .cm-tooltip.cm-tooltip-autocomplete > ul > li[aria-selected] {
    background: var(--chakra-colors-gray-50) !important;  /* Selected item background color */
}


